mapper.select:
	select count(*)
	from sysuser
	
		
			and username like '%${username}%'
		
		
			and account = '${account}'
	    
        
        	and user_type = #{userType,jdbcType=INTEGER}
        
        
        	and department_id = #{deptId,jdbcType=INTEGER}

		select * from sysuser
		
			
				and username like '%${username}%'
			
			
				and account = '${account}'
		    
	        
	        	and user_type = #{userType,jdbcType=INTEGER}
	        
	        
	        	and department_id = #{deptId,jdbcType=INTEGER}
	        
	 	
    	
    		limit ${start},${count}
    	
		
   			order by #{orderSql}
